import { Component } from '@angular/core';
import { EmployeeService } from '../employee.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-employee',
  templateUrl: './create-employee.component.html',
  styleUrls: ['./create-employee.component.css']
})
export class CreateEmployeeComponent implements OnInit{
constructor(private service:EmployeeService,private router:Router)
}
<div class="col-md-6 offset-md-3">
<h1>Create Employee Record</h1>
<form (ngSubmit)="onSubmit()">
<div class="form-group">
<label for="name">Name</label>
<input type="text" class="form-control" id="name" name="name" [(ngModel)]="employee.name">
<label for="address">Address</label>
<input type="text" class="form-control" id="address" name="address" [(ngModel)]="employee.address">
<label for="salary">Salary</label>
<input type="number" class="form-control" id="salary" name="salary" [(ngModel)]="employee.salary"> <br>
<button class="btn btn-success" type="submit">Submit</button>
</div>
</form>
</div>
